import React from 'react';
import Profile from './profile';
import { connect } from 'react-redux';
import { withRouter, Redirect } from 'react-router-dom';
import { getUserProfile } from '../../redux/profile-reducer';
import { withAuthRedirect } from '../../hoc/auth-redirect';



class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = 12930;
        }
        this.props.getUserProfile(userId);
    }

    render () {
        if (this.props.isAuth === false) return <Redirect to='/login' />;
        return (
            <div>
                <Profile { ...this.props } profile={ this.props.profile }/>

          </div>
        )
    }
};

let AuthRedirecComponent = withAuthRedirect(ProfileContainer);

AuthRedirecComponent = connect(mapStateToPropsForRedirect)(AuthRedirecComponent)

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile,
});

let WithUrlDataContainerComponent = withRouter(AuthRedirecComponent);

export default connect(mapStateToProps, { getUserProfile } )(WithUrlDataContainerComponent);
