const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users: [
        { id: 1, photoUrl: 'https://i.pinimg.com/564x/4d/77/e2/4d77e2ffb4b616928c6242b94f6cfefd.jpg', followed: true, fullName: 'Luda', status: 'I am fine', location: { country: 'Ukraine', city: 'Kiev' } },
        { id: 2, photoUrl: 'https://i.pinimg.com/564x/4d/77/e2/4d77e2ffb4b616928c6242b94f6cfefd.jpg', followed: false, fullName: 'Sasha', status: 'I am fine too', location: { country: 'Ukraine', city: 'Kiev' } },
        { id: 3, photoUrl: 'https://i.pinimg.com/564x/4d/77/e2/4d77e2ffb4b616928c6242b94f6cfefd.jpg', followed: true, fullName: 'Vika', status: 'I am fine too', location: { country: 'Ukraine', city: 'Kiev' } }
    ]
}

const usersReducer = (state = initialState, action) => {

    switch(action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map( u => {
                    if (u.id === action.userId) {
                        return {  ...u, followed: true }
                    }
                    return u;
                })
            }          

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map( u => {
                    if (u.id === action.userId) {
                        return { ...u, followed: false }
                    }
                    return u;
                })
            }

        case SET_USERS: {
            return { ...state, users: [...state.users, ...action.users] }
        }

        default: 
        return state;

    }
};

export const followAC = (userId) => ({ type: 'FOLLOW', userId });
export const unfollowAC = (userId) => ({ type: 'UNFOLLOW', userId });
export const setUsersAC = (users) => ({ type: 'SET_USERS', users });

export default users;
