import React, { useState } from 'react';

const ProfileStatusWithHooks = (props) => {

    let [editMode, setEditMode] = useState(false);

    const activateMode = () => {
        setEditMode(true);
    }

    return (
        <div>
            { !editMode && 
            <div>
                <span onDoubleClick={ activateMode }>{ props.status || '------' }</span>
            </div>
            }
            { editMode}
        </div>
    )
}

export default ProfileStatusWithHooks;
