import React from 'react';
import * as axios from 'axios';
import styles from './users.module.css';
import image from './users-image.jpg';
import { NavLink } from 'react-router-dom';
import { subscriptionAPI } from '../../api/api';


const Users = (props) => {

    const pagesCount = Math.ceil (props.totalUsersCount / props.pageSize);

    let pages = [];
    for (let i=1; i <= pagesCount; i++ ) {
        pages.push(i);
    }

    return <div>
        <div>
            { pages.map( p  => {
                return <span className={ props.currentPage === p ? styles.selectedPage : styles.page }
                            onClick={ () => { props.onPageChanged(p) }  }
                            key={ p.id }>{ p }</span>
            })}
        </div>
            { 
                props.users.map( u => <div key={ u.id }>
                    <span>
                        <div>
                            <NavLink to={ '/profile/' + u.id }>
                            <img src={ u.photos.small != null ? u.photos.small : image } 
                                alt='avatar' className={ styles.userPhoto }/>
                            </NavLink>
                        </div>
                        <div>
                            { u.followed 
                            ? <button onClick={ () => { 
                                    subscriptionAPI.setSubscription(props.users.idid).then(data => {
                                                if (data.resultCode === 0) {
                                                    props.unfollow(u.id);
                                                }
                                    }); 
                                } }>Unfollow</button>

                            : <button onClick={ () => { 
                                    axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${ u.id }`, {}, {
                                        withCredentials: true,
                                        headers: {
                                            'API-KEY': '64f8be29-cf1d-466a-89b4-ad2dfe68ef0e'
                                        }
                                    })
                                            .then(response => {
                                                if (response.data.resultCode === 0) {
                                                    props.follow(u.id); 
                                                }
                                    });
                                } }>Follow</button>}
                        </div>
                    </span>
                    <span>
                        <span>
                            <div>{ u.name }</div>
                            <div>{ u.status }</div>
                        </span>
                        <span>
                            <div>{ 'u.location.city' }</div>
                            <div>{ 'u.location.country' }</div>
                        </span>
                    </span>
                </div>)
            }
    </div>
}


export default Users;
