import React from 'react';
import styles from './form-controls.module.css';

export const Textarea = ({ input, meta, ...props }) => {
    return (
        <div className={ styles.formControl + '' + styles.error }>
            <div><textarea { ...input } { ...props }/></div>
        </div>
    )
}
