import React from 'react';
import styles from './users.module.css';
import image from './users-image.jpg';
import { NavLink } from 'react-router-dom';
import Paginator from './paginator';


const Users = ({ currentPage, totalUsersCount, pageSize, }) => {

    const pagesCount = Math.ceil (props.totalUsersCount / props.pageSize);

    let pages = [];
    for (let i=1; i <= pagesCount; i++ ) {
        pages.push(i);
    }

    return <div>
        {/* <div>
            { pages.map( p  => {
                return <span className={ props.currentPage === p ? styles.selectedPage : styles.page }
                            onClick={ () => { props.onPageChanged(p) }  }
                            key={ p }>{ p }</span>
            })}
        </div> */}
        <Paginator />
        { 
            props.users.map( u => <div key={ u.id }>
                <span>
                    <div>
                        <NavLink to={ '/profile/' + u.id }>
                        <img src={ u.photos.small != null ? u.photos.small : image } 
                            alt='avatar' className={ styles.userPhoto }/>
                        </NavLink>
                    </div>
                    <div>
                        { u.followed 
                            ? <button disabled={ props.followingInProgress
                                .some( id => id === u.id) } 
                                    onClick={ () => { props.unfollow(u.id); } }>
                                    Unfollow</button>

                            : <button disabled={ props.followingInProgress
                                .some( id => id === u.id) } 
                                    onClick={ () => { props.follow(u.id) } }>
                                    Follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{ u.name }</div>
                        <div>{ u.status }</div>
                    </span>
                    <span>
                        <div>{ 'u.location.city' }</div>
                        <div>{ 'u.location.country' }</div>
                    </span>
                </span>
            </div>)
        }
    </div>
}


export default Users;
