import React from 'react';

class ProfileStatus extends React.Component {

    state = {
        editMode: false
    }

    activateEditMode() {
        this.state.editMode = true;
    }

    render() {
        return (
            <div>
            { !this.state.editMode &&
            <div>
                <span onDoubleClick={ () => {this}}>{ this.props.status }</span>
            </div>
            }
            { this.state.editMode &&
            <div>
                <input value={ this.props.status }></input>
            </div>
            }
            </div>
        )
    }
}

export default ProfileStatus;
