import React from 'react';
import s from './dialogs.module.css';
import DialogItem from './dialog-item';
import Message from './message';
import { addMessageActionCreator, updateNewMessageBodyActionCreator } from '../../redux/state';


const Dialogs = (props) => {

    let dialogsElements = props.dialogs
        .map( d => <DialogItem name={ d.name } id={ d.id } />
    );

    let messagesElements = props.messages
        .map( m => <Message text={ m.text } id={ m.id } />
        );

    let newMessageElement = React.createRef();

    let sendMessage = () => {
        // let text = newMessageElement.current.value;
        props.dispatch(addMessageActionCreator());
        props.updateNewMessageBody('');
    };

    let onMessageChange = () => {
        let text = newMessageElement.current.value;
        props.dispatch(updateNewMessageBodyActionCreator(text));
    };

    return(
        <div className={ s.dialogs }>
            <div className={ s.dialogsItems }>
               { dialogsElements }
            </div>
            <div className={ s.messages }>
                { messagesElements }
                <div><textarea ref= { newMessageElement } value={ props.newMessageBody } onChange={ onMessageChange }></textarea>
                <button onClick={ sendMessage }>Send</button>
                </div>
            </div>
        </div>
    )
};

export default Dialogs;
