import React from 'react';
import { reduxForm } from 'redux-form';
import s from './my-posts.module.css';

import Post from './post'; 

const addNewPostForm = (props) => {
    return (
        <form onSubmit={ props.handleSubmit }>
                <div>
                    <textarea ref={ props.ref } value={ props.value } onChange={ props.onChange }/>
                </div>
                <div className={ s.button }>
                    <button onClick={ props.onClick }>Add post</button>
                </div>
            </form>
    )
};

addNewPostForm = reduxForm({ form: 'ProfileAddNewPostForm' })()

const MyPosts = (props) => {

    let postsElements = props.posts
        .map( p => <Post message={ p.message } likesCount={ p.likes } key={ p.id }/>
        );

    let newPostElement = React.createRef();

    let onAddPost = (values) => {
        props.addPost();
    }

       return(
        <div className={ s.myPosts }>
            <h3>My Posts</h3>
            <addNewPostForm onSubmit={ onAddPost }/>
                <div className={ s.posts }>
                    { postsElements }
                </div>
        </div>
    )
};

export default MyPosts;
